import React from 'react';
import axios from 'axios';
import { Button, Form, Card} from "react-bootstrap";
class Home extends React.Component {
  constructor(props){
    super(props)
    this.state= {
      opis: '',
      uspjeh: ''
    };
  } 

  handleInputChange = (event) => {
    event.preventDefault()
      this.setState({
        [event.target.name] : event.target.value
      });
  };
  handleClick = (e) => {
    e.preventDefault();
    axios.get("http://localhost:5000/employees/:").then((res)=> {
        this.setState({
            uspjeh: res.data,
        });
        this.state.uspjeh = "";
    })
    .catch(()=> {
        this.setState({ 
            searchBar: "",
            uspjeh: ""
        });
    });
}
   render() {
    return (
      <div className="main-div" id="div">
          <Card style={{ width: '55rem' }} id="prva">
              <Card.Body>
                <Card.Title id="naslov"> Secret santa </Card.Title>
                <Card.Text id="tekst">
                  Match
                </Card.Text>
                  <Form>
                    <Button onClick={this.handleClick}  id="search">Match</Button>
                  </Form>
                  <Button href="/addemployee"  id="add"> Add new employee </Button>
                  
                  <span className="poruka">{this.state.uspjeh}</span>
                  
              </Card.Body>
            </Card>
      </div>
    );
  }
}
export default Home;
