import React from 'react';
import axios from 'axios';
import { Button, Form, Card} from "react-bootstrap";
import Paper from '@material-ui/core/Paper';
import Hidden from '@material-ui/core/Hidden';
import ReactDOMServer from "react-dom/server";
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
class Home extends React.Component {
  constructor(props){
    super(props)
    this.state= {
      opis: '',
      uspjeh: ''
    };
  } 

  handleInputChange = (event) => {
    event.preventDefault()
      this.setState({
        [event.target.name] : event.target.value
      });
  };
  handleClick = (e) => {
    e.preventDefault();
    axios.get("http://localhost:5000/employees/:").then((res)=> {
        this.setState({
            uspjeh: res.data,
        });
        this.state.uspjeh = "";
    })
    .catch(()=> {
        this.setState({ 
            searchBar: "",
            uspjeh: ""
        });
    });
  }
  _exportPdf = () => {

    html2canvas(document.querySelector("#divToPrint")).then(canvas => {
       const imgData = canvas.toDataURL('image/png');
       const pdf = new jsPDF();
       pdf.addImage(imgData, 'PNG', 0, 0);
       pdf.save("download.pdf"); 
   });

}


   render() {
    return (
      <div className="main-div" id="div">
          <Card style={{ width: '55rem' }} id="prva">
              <Card.Body>
                <Card.Title id="naslov"> Secret santa </Card.Title>
                <Card.Text id="tekst">
                  Match
                </Card.Text>
                  <Form>
                    <Button onClick={this.handleClick}  id="search">Match</Button>
                  </Form>
                  <Button href="/addemployee"  id="add"> Add new employee </Button>
                   
                  <div id='divToPrint'>
                  <label> Report</label>
                  <Hidden >
                    <Paper id="message"> {this.state.uspjeh}</Paper>
                  </Hidden>
                  <Button onClick={this._exportPdf} > Export as pdf</Button>
                   </div>
 
              </Card.Body>
            </Card>
      </div>
    );
  }
}


export default Home;
