import React from 'react';
import { Button, Form,  Card} from "react-bootstrap";
import axios from 'axios';

class AddEmployee extends React.Component {
  constructor(props){
    this.state={
      word: '',
      synonym: '',
      uspjeh: '',
      opis: ''
    };
  } 
 handleSubmit = (event) => {
    event.preventDefault();
    let reqBody = {
      word: this.state.word,
      synonym: this.state.synonym
    };
    axios.post("http://localhost:5000/employees", reqBody)
         .then(res=> { 
          this.setState({
            name: "",
            surname: "",
            uspjeh: res.data
          });
          this.state.uspjeh = "";
         })
         .catch(error => {
            this.setState({ 
              name: "",
             surname: ""
        });

    });
  }
  /*componentDidMount() {
    axios.get('http://localhost:5000/sinonimi')
      .then(response => this.setState({items: response.data}))
      .catch(err => { 
        this.setState({errorMessage: err.message});
      })
  }*/
  handleInputChange = (event) => {
    event.preventDefault()
      this.setState({
        [event.target.name] : event.target.value
      });
  };
 
  render() {
    const { name, surname} = this.state
  
    return (
      <div className="main-div" id="div">
            <Card style={{ width: '55rem' }} id="prva">
              <Card.Body>
                <Card.Title id="naslov"> Add new employee </Card.Title>
    
                  <Form onSubmit= {this.handleSubmit}  id="forma" >
                    <Form.Text className="label"> Add a name</Form.Text>
                    <Form.Control type="text1" placeholder="Enter a name" className="bar" id="input1" name= "name" value={name} onChange= {this.handleInputChange}/>
                    <Form.Text className="label"> Add a surname</Form.Text>
                    <Form.Control type="text2" placeholder="Enter synonyms with whitespaces" className="bar" id="input2" name= "surname" value={surname} onChange= {this.handleInputChange}/>
                    <Button type="submit" variant="warning" id="save" >Save</Button>
                    <Button href="/"variant="warning" id="search1"> Search </Button>
                  </Form>
                  
                  <span className="poruka">{this.state.uspjeh}</span>
              </Card.Body>
            </Card>
      </div>
    );
  }
}

export default AddEmployee;
